import React, { useState } from 'react';
import Header from '../Header/Header'; // Corrected path
import Navigation from '../Navigation/Navigation'; // Corrected path
import Table from '../Table/Table'; // Corrected path
import Footer from '../Footer/Footer'; // Corrected path

const AuditPage = () => {
  const tabs = ['Inbound', 'Staging', 'Loading', 'Parking', 'Module 5', 'Module 6', 'Module 7'];
  const [activeTab, setActiveTab] = useState('Inbound');
  const [data, setData] = useState([
    { attribute: 'Induct mechanisms', currentValue: 14, actualValue: '', comments: '', flagAsKDI: false },
    { attribute: 'Induct stations', currentValue: 5, actualValue: '', comments: '', flagAsKDI: false },
    { attribute: 'Staging locations', currentValue: 16, actualValue: '', comments: '', flagAsKDI: false },
    { attribute: 'No of dock doors', currentValue: 24, actualValue: '', comments: '', flagAsKDI: false },
    // ... more rows
  ]);
  useEffect(() => {
    const fetchData = async () => {
      try { //${activeTab}
        const response = await fetch(`http://localhost:8000/get/DQQ1`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const fetchedData = await response.json();

        // Update the state with the new values from the fetched data
        setData(currentData =>
          currentData.map(item => ({
            ...item,
            currentValue: fetchedData[item.attribute] || item.currentValue, // Replace with the new value, or fallback to the current value if undefined
            actualValue: fetchedData[item.attribute] || item.actualValue,   // Replace with the new value, or fallback to the current value if undefined
            // You can add additional fields to update as needed
          }))
        );

      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    };fetchData();
  }, [activeTab]);
  // Handlers for Footer
  const handleHoldDeployment = () => {
    // Logic to hold deployment
  };

  const handleSave = () => {
    // Logic to save data
  };

  const handleComplete = () => {
    // Logic to mark as complete
  };

  return (
    <>
      <Header />
      <Navigation tabs={['Inbound', 'Staging', 'Loading', 'Parking', 'Module 5', 'Module 6', 'Module 7']} activeTab={activeTab} setActiveTab={setActiveTab} />
      <div className="content">
        <Table data={data} setData={setData} />
      </div>
      <Footer />
      {/* ... */}
    </>
  );
};

export default AuditPage;
